slides.code:
% Step 2: Define the function
f = (x^2 - 1) / (x - 1);
disp('The function f(x) = ');
disp(f)

% Step 3: Compute limit as x approaches 1
limit_value = limit(f, x, 1);
fprintf('Limit as x approaches 1: %s\n', char(limit_value));

% Step 4: Compute left and right limits
left_limit = limit(f, x, 1, 'left');
right_limit = limit(f, x, 1, 'right');
fprintf('Left limit: %s, Right limit: %s\n', char(left_limit), char(right_limit));

% Step 5: Compute limit as x approaches infinity
g = (2*x^2 + 3*x + 1) / (x^2 + 1);
infinity_limit = limit(g, x, inf);
fprintf('Limit as x approaches infinity: %s\n', char(infinity_limit));

% Step 6: Check continuity at a point
h = x^2 + 1;
% Check if h is continuous at x = 2
point_value = subs(h, x, 2);
limit_at_point = limit(h, x, 2);
fprintf('h(2) = %s, Limit = %s\n', char(point_value), char(limit_at_point));

% Step 7: Function with jump discontinuity
k = piecewise(x < 0, -1, x >= 0, 1);
left_limit_k = limit(k, x, 0, 'left');
right_limit_k = limit(k, x, 0, 'right');
fprintf('Left limit: %s, Right limit: %s\n', char(left_limit_k), char(right_limit_k));

% Step 8: Squeeze theorem example
% -1 ≤ sin(x)/x ≤ 1 for x ≠ 0
syms n
squeeze_expr = sin(n)/n;
squeeze_limit = limit(squeeze_expr, n, inf);
fprintf('Limit using squeeze theorem: %s\n', char(squeeze_limit));

% Step 9: L'Hôpital's rule example
% lim(x→0) (sin(x))/x
lhopital_expr = sin(x)/x;
lhopital_limit = limit(lhopital_expr, x, 0);
fprintf('Limit using L''Hôpital''s rule: %s\n', char(lhopital_limit));

% Step 10: Check continuity on an interval
% f(x) = x² is continuous on [-1, 1]
x_vals = -1:0.1:1;
y_vals = x_vals.^2;
fprintf('f(x) = x² is continuous on [-1, 1]\n');
fprintf('f(-1) = %.2f, f(1) = %.2f\n', y_vals(1), y_vals(end));
