% Step 2: Define the function to integrate
f = x^2 + 3*x + 1;
disp('The function to integrate f(x) = ');
disp(f)

% Step 3: Compute the indefinite integral
F = int(f);
fprintf('Indefinite integral ∫f(x)dx = %s + C\n', char(F));

% Step 4: Compute definite integral from 0 to 2
a = 0;
b = 2;
result = int(f, x, a, b);
fprintf('Definite integral from %d to %d = %s\n', a, b, char(result));

% Step 5: Numeric integration using quad
f_numeric = @(x) x.^2 + 3*x + 1;
result_numeric = quad(f_numeric, 0, 2);
fprintf('Numeric result: %.6f\n', result_numeric);

% Step 6: Integration by parts example
syms u v
u = x;
v = exp(x);
% ∫u*dv = u*v - ∫v*du
result_parts = u*v - int(v*diff(u));
disp('Integration by parts result:');
disp(result_parts);

% Step 7: Trigonometric integration
syms t
g = sin(t)^2;
G = int(g);
fprintf('∫sin²(t)dt = %s\n', char(G));

% Step 8: Double integration example
syms x y
h = x*y;
% Double integral ∫∫h(x,y)dxdy
result_double = int(int(h, x), y);
fprintf('Double integral result: %s\n', char(result_double));
